import { useState } from "react";

export default function Register(){
    var [name, setName] = useState("");
    var [email, setEmail] = useState("");
    var [password, setPassword] = useState("");
    var [contact, setContact] = useState("");
    var [address, setAddress] = useState("");
    var [profession, setProfession] = useState("");
    var [emailError, setEmailError] = useState("");
    var [contactError, setContactError] = useState("");

    const handleform = (e)=>{
        console.log("form cannot be submitted empty");
        e.preventDefault();
    }

    return(
        <>
          <div className=" container-fluid py-5">
            <div className="conatiner py-3">
                <h1 className="text-center">Registeration Form</h1>
            </div>
            <div className="container w-50 py-3 mx-auto">
                <form onSubmit={handleform}>
                    <div className="form-group">
                        <label for="name">Name:</label>
                        <input
                          type="text"
                          className="form-control my-1"
                          id="name"
                          value={name}
                          onChange={(e) => setName(e.target.value)}
                          placeholder="Enter your name"
                        />
                    </div>
                    <div className="form-group">
                        <label for="name">Email:</label>
                        <input
                          type="text"
                          className="form-control my-1"
                          id="email"
                          value={email}
                          onChange={(e) => setEmail(e.target.value)}
                          placeholder="Enter your email"
                        />
                    </div>
                    <div className="form-group">
                        <label for="name">Password:</label>
                        <input
                          type="text"
                          className="form-control my-1"
                          id="password"
                          value={password}
                          onChange={(e) => setPassword(e.target.value)}
                          placeholder="Set Password"
                        />
                    </div>
                    <div className="form-group">
                        <label for="name">Contact:</label>
                        <input
                          type="text"
                          className="form-control my-1"
                          id="contact"
                          value={contact}
                          onChange={(e) => setContact(e.target.value)}
                          placeholder="Set Password"
                        />
                    </div>
                    <div className="form-group">
                        <label for="name">Address:</label>
                        <input
                          type="text"
                          className="form-control my-1"
                          id="address"
                          value={address}
                          onChange={(e) => setAddress(e.target.value)}
                          placeholder="Set Password"
                        />
                    </div>
                    <div className="form-group">
                        <button type="submit" className="btn btn-primary">Register</button>
                    </div>
                </form>
            </div>
          </div>
        </>
    );
}
